         हमारे बारे मेंपत्र सूचना कार्यालय के बारे मेंपीआईबी के मुख्य कार्यपत्र सूचना कार्यालय (पीआईबी) का संगठनात्मक ढांचापीआईबी के वर्तमान और पूर्व प्रमुखमुख्यालय के पीआईबी अधिकारियों के टेलीफोन नंप्रेस विज्ञप्तिसभी प्रेस विज्ञप्तिपीएमओ प्रेस विज्ञप्ति2004 से 2017 तक की विज्ञप्तियांगैलरीफ़ोटोवीडियोई-पुस्तिकाइन्फोग्राफिकवेबकास्ट वीडियोइवेंटअनुसंधान इकाईविश्लेषकतथ्य-पत्रएफएक्यूफीचरअमृत यात्राविकसित भारत संकल्प यात्रामीडिया सुविधाएंप्रत्यायनसुविधाएंमीडिया निमंत्रणमीडिया सूचीजेडब्ल्यूएसआर्काइवअभिलेखागार के बारे में1947-20012002-20032004 के बादतथ्यों की जांचतथ्यों की जांच के बारे मेंतथ्यों की जांच के लिए जमा करेंएफ ए क्यूसंख्याओं में तथ्य की जाँचतथ्यों की जांच की गईधोखाधड़ी की वेबसाइटें

    .menuchang {
        background-image:url('images/new.gif')  ;
        background-repeat: no-repeat;
        padding-right:5px;
        background-position:top;
    }
     .fa-home_r::after {
        content: url(https://static.pib.gov.in/WriteReadData/userfiles/f_hom.png);
        position: relative;
        z-index: 100000;
        top: 0;
        bottom: 0;
    }


    // Function to close all open dropdown menus
    function closeDropdowns() {
        // Select all open dropdowns by checking if 'display: block;' or similar CSS is applied
        var openDropdowns = document.querySelectorAll('.has-sub > ul[style*="display: block"]');

        // Loop through each open dropdown and set display to 'none'
        openDropdowns.forEach(function (dropdown) {
            dropdown.style.display = 'none';
        });
    }

    // Event listener for keydown event to close dropdowns with Escape key
    document.addEventListener('keydown', function (event) {
        if (event.key === 'Escape') {
            closeDropdowns();  // Close all dropdowns when Escape is pressed
        }
    });















